const counterReducer = (state = 0, action) => {
  console.log(action.type, "action.type :) ");

  switch (action.type) {
    case 'INCREMENT':
      console.log('');
      return state + action.payload;

    case 'DECREMENT':
      return state - 1;

    default:
      console.log(`no reducer case set up for action.type: ${action.type}`);
      return state;
  }
};

export default counterReducer;
